CREATE TABLE E`bookmark` ( E`id` int(10) unsigned NOT NULL auto_increment, E`user_id` int(10) unsigned NOT NULL, E`link_id` int(10) unsigned NOT NULL, E`title` text collate utf8_bin NOT NULL, E`notes` text collate utf8_bin NOT NULL, E`created_on` datetime NOT NULL, E`last_updated` datetime NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`bookmark_user_id_link_id_idx` USING BTREE (E`user_id`,E`link_id`), KEY E`bookmark_user_id_idx` (E`user_id`), KEY E`bookmark_link_id` USING BTREE (E`link_id`), KEY E`bookmark_created_on_idx` (E`created_on`), KEY E`bookmark_lastup_idx` (E`last_updated`), CONSTRAINT E`FK_bookmark_1` FOREIGN KEY (E`user_id`) REFERENCES E`user` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_bookmark_2` FOREIGN KEY (E`link_id`) REFERENCES E`link` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=DYNAMIC;
CREATE TABLE E`bookmark_folder` ( E`id` int(10) unsigned NOT NULL auto_increment, E`bookmark_id` int(10) unsigned NOT NULL, E`folder_id` int(10) unsigned NOT NULL, E`last_updated` datetime NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`bookmark_folder_unique_bmark` (E`bookmark_id`,E`folder_id`), KEY E`FK_bookmark_folder_1` (E`bookmark_id`), KEY E`FK_bookmark_folder_2` (E`folder_id`), KEY E`bookmark_folder_lastup_idx` (E`last_updated`), CONSTRAINT E`FK_bookmark_folder_1` FOREIGN KEY (E`bookmark_id`) REFERENCES E`bookmark` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_bookmark_folder_2` FOREIGN KEY (E`folder_id`) REFERENCES E`folder` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
CREATE TABLE E`bookmark_point_marker_idx` ( E`bookmark_id` int(10) unsigned NOT NULL, E`point_marker_id` int(10) unsigned NOT NULL, PRIMARY KEY (E`bookmark_id`,E`point_marker_id`), CONSTRAINT E`FK_bookmark_point_mark_idx_1` FOREIGN KEY (E`bookmark_id`) REFERENCES E`bookmark` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE E`bookmark_tag_idx` ( E`id` int(10) unsigned NOT NULL auto_increment, E`bookmark_id` int(10) unsigned NOT NULL, E`tag_id` int(10) unsigned NOT NULL, E`count` int(10) unsigned NOT NULL, E`position` smallint(5) unsigned NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`Index_4` (E`bookmark_id`,E`tag_id`), KEY E`Index_2` (E`bookmark_id`), KEY E`Index_3` (E`tag_id`), CONSTRAINT E`FK_bookmark_tag_idx_1` FOREIGN KEY (E`bookmark_id`) REFERENCES E`bookmark` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_bookmark_tag_idx_2` FOREIGN KEY (E`tag_id`) REFERENCES E`tag` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
CREATE TABLE E`feed_folder` ( E`id` int(10) unsigned NOT NULL auto_increment, E`feed_id` int(10) unsigned NOT NULL, E`folder_id` int(10) unsigned NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`feed_folder_unique_entry` (E`feed_id`,E`folder_id`), KEY E`FK_feed_folder_2` (E`folder_id`), KEY E`FK_feed_folder_1` USING BTREE (E`feed_id`), CONSTRAINT E`FK_feed_folder_1` FOREIGN KEY (E`feed_id`) REFERENCES E`feed_subscription` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_feed_folder_2` FOREIGN KEY (E`folder_id`) REFERENCES E`folder` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE E`feed_subscription` ( E`id` int(10) unsigned NOT NULL auto_increment, E`bookmark_id` int(10) unsigned NOT NULL COMMENT 'ref a bmark that defines a RSS feed', E`last_sync` datetime default NULL COMMENT 'when was the last time that this feed was read', E`match_text` text character set utf8 NOT NULL COMMENT 'space-delimited tag labels that a bmark importer will use to match feed entries that should imported as bookmarks', E`auto_import` tinyint(1) NOT NULL default '1', E`pub_date` datetime default NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`feed_sub_unique_bm_id` (E`bookmark_id`), KEY E`feed_sub_last_sync_idx` (E`last_sync`), CONSTRAINT E`FK_feed_subscription_1` FOREIGN KEY (E`bookmark_id`) REFERENCES E`bookmark` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=DYNAMIC;
CREATE TABLE E`folder` ( E`id` int(10) unsigned NOT NULL auto_increment, E`folder_name` varchar(45) collate utf8_bin NOT NULL, E`owner_id` int(10) unsigned NOT NULL, E`description` text collate utf8_bin NOT NULL, E`last_updated` datetime NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`folder_owner_folder_idx` USING BTREE (E`folder_name`,E`owner_id`), KEY E`FK_folder_admin_1` (E`owner_id`), CONSTRAINT E`FK_folder_admin_1` FOREIGN KEY (E`owner_id`) REFERENCES E`user` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
CREATE TABLE E`folder_tag_idx` ( E`folder_id` int(10) unsigned NOT NULL, E`tag_id` int(10) unsigned NOT NULL, E`count` int(10) unsigned NOT NULL, PRIMARY KEY (E`folder_id`,E`tag_id`), KEY E`FK_folder_tag_idx_2` (E`tag_id`), CONSTRAINT E`FK_folder_tag_idx_1` FOREIGN KEY (E`folder_id`) REFERENCES E`folder` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_folder_tag_idx_2` FOREIGN KEY (E`tag_id`) REFERENCES E`tag` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=latin1;
CREATE TABLE E`for_user` ( E`id` int(10) unsigned NOT NULL auto_increment, E`for_user_id` int(10) unsigned NOT NULL, E`bookmark_id` int(10) unsigned NOT NULL, E`message` text NOT NULL, E`created_on` datetime NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`for_user_index_1` (E`for_user_id`,E`bookmark_id`), KEY E`FK_for_user_1` (E`for_user_id`), KEY E`FK_for_user_2` (E`bookmark_id`), CONSTRAINT E`FK_for_user_1` FOREIGN KEY (E`for_user_id`) REFERENCES E`user` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_for_user_2` FOREIGN KEY (E`bookmark_id`) REFERENCES E`bookmark` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE E`link` ( E`id` int(10) unsigned NOT NULL auto_increment, E`mime_type_id` int(10) unsigned default NULL, E`url` text NOT NULL, E`url_hash` varchar(45) NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`url_hash_idx` USING BTREE (E`url_hash`), KEY E`FK_link_1` (E`mime_type_id`), CONSTRAINT E`FK_link_1` FOREIGN KEY (E`mime_type_id`) REFERENCES E`mime_type_admin` (E`id`) ON DELETE SET NULL ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`link_tag_idx` ( E`id` int(10) unsigned NOT NULL auto_increment, E`link_id` int(10) unsigned NOT NULL, E`tag_id` int(10) unsigned NOT NULL, E`count` int(10) unsigned zerofill NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`idx_link_id_tag_id` (E`link_id`,E`tag_id`), KEY E`FK_link_tag_idx_2` (E`tag_id`), KEY E`idx_link_id` (E`link_id`), CONSTRAINT E`FK_link_tag_idx_1` FOREIGN KEY (E`link_id`) REFERENCES E`link` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_link_tag_idx_2` FOREIGN KEY (E`tag_id`) REFERENCES E`tag` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`mime_type_admin` ( E`id` int(10) unsigned NOT NULL default '0', E`mime_type` varchar(45) NOT NULL, E`label` varchar(100) NOT NULL, PRIMARY KEY (E`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`point_marker` ( E`id` int(10) unsigned NOT NULL auto_increment, E`geom` point NOT NULL, E`notes` varchar(255) NOT NULL, E`icon_id` tinyint(3) unsigned NOT NULL default '0', PRIMARY KEY (E`id`), SPATIAL INDEX(E`geom`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE E`search_idx` ( E`bookmark_id` int(10) unsigned NOT NULL, E`text` text NOT NULL, E`tags` text NOT NULL, PRIMARY KEY (E`bookmark_id`), FULLTEXT KEY E`search_idx_text` (E`text`), FULLTEXT KEY E`search_idx_tags` (E`tags`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE E`tag` ( E`id` int(10) unsigned NOT NULL auto_increment, E`tag` varchar(45) collate utf8_bin NOT NULL, E`count` int(10) unsigned zerofill NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`idx_tag` (E`tag`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin ROW_FORMAT=DYNAMIC;
CREATE TABLE E`tag_assertion` ( E`id` int(10) unsigned NOT NULL auto_increment, E`subject_id` int(10) unsigned NOT NULL, E`prpt_id` int(10) unsigned NOT NULL, E`object_id` int(10) unsigned NOT NULL, E`user_id` int(10) unsigned NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`tag_asrt_uniq_asrt_idx` (E`subject_id`,E`prpt_id`,E`object_id`,E`user_id`), KEY E`FK_tag_asrt_prpt_id` USING BTREE (E`prpt_id`), KEY E`FK_tag_asrt_subject_id` USING BTREE (E`subject_id`), KEY E`FK_tag_asrt_object_id` USING BTREE (E`object_id`), KEY E`FK_tag_asrt_user_id` USING BTREE (E`user_id`), CONSTRAINT E`FK_tag_asrt_object_id` FOREIGN KEY (E`object_id`) REFERENCES E`user_tag_idx` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_tag_asrt_prpt_id` FOREIGN KEY (E`prpt_id`) REFERENCES E`tag_prpt` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_tag_asrt_subject_id` FOREIGN KEY (E`subject_id`) REFERENCES E`user_tag_idx` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_tag_asrt_user_id` FOREIGN KEY (E`user_id`) REFERENCES E`user` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`tag_prpt` ( E`id` int(10) unsigned NOT NULL auto_increment, E`name` varchar(45) NOT NULL, E`ns_prefix` varchar(10) NOT NULL default 'gn', E`description` varchar(255) default NULL, E`prpt_type` enum('spatial','temporal','system','reference','default') NOT NULL default 'default', E`cardinality` int(11) NOT NULL default '-1', PRIMARY KEY (E`id`), UNIQUE KEY E`tag_prpt_unique_prpt_idx` (E`name`,E`ns_prefix`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`user` ( E`id` int(10) unsigned NOT NULL auto_increment, E`username` varchar(45) NOT NULL, E`password` varchar(45) NOT NULL, E`fullname` varchar(100) NOT NULL, E`created_on` datetime NOT NULL, E`email` varchar(50) NOT NULL, E`acct_status` int(10) unsigned zerofill NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`idx_username` (E`username`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
CREATE TABLE E`user_tag_idx` ( E`id` int(10) unsigned NOT NULL auto_increment, E`user_id` int(10) unsigned NOT NULL, E`tag_id` int(10) unsigned NOT NULL, E`count` int(10) unsigned zerofill NOT NULL, PRIMARY KEY (E`id`), UNIQUE KEY E`idx_user_id_tag_id` (E`user_id`,E`tag_id`), KEY E`FK_user_tag_idx_2` (E`tag_id`), KEY E`idx_user_tag_id_user_id` (E`user_id`), CONSTRAINT E`FK_user_tag_idx_1` FOREIGN KEY (E`user_id`) REFERENCES E`user` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE, CONSTRAINT E`FK_user_tag_idx_2` FOREIGN KEY (E`tag_id`) REFERENCES E`tag` (E`id`) ON DELETE CASCADE ON UPDATE CASCADE ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;
